<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>My Playground</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <script defer src="https://unpkg.com/alpinejs@3.x.x/dist/cdn.min.js"></script>
    <style>
        .fade-in {
            animation: fadeIn 1s ease-in;
        }

        @keyframes fadeIn {
            0% {
                opacity: 0;
            }

            100% {
                opacity: 1;
            }
        }
    </style>
</head>

<body class="bg-[#2b4581] fade-in" x-data="{ isOpen: false }">
    <!-- Navbar -->
    <!-- Hamburger Button -->
    <div class="fixed top-4 right-4 z-50">
        <button @click="isOpen = !isOpen" class="text-white p-2 rounded-lg bg-gray-800 hover:bg-gray-700">
            <svg class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 6h16M4 12h16m-16 6h16">
                </path>
            </svg>
        </button>
    </div>

    <!-- Sidebar -->
    <div x-show="isOpen" @click.away="isOpen = false" x-transition:enter="transition ease-out duration-300"
        x-transition:enter-start="opacity-0 transform translate-x-full"
        x-transition:enter-end="opacity-100 transform translate-x-0"
        x-transition:leave="transition ease-in duration-300"
        x-transition:leave-start="opacity-100 transform translate-x-0"
        x-transition:leave-end="opacity-0 transform translate-x-full"
        class="fixed top-0 right-0 h-full w-64 bg-gray-800 text-white shadow-lg z-40">

        <div class="p-4">
            <h3 class="text-xl font-bold mb-4">Menu</h3>
            <nav class="space-y-3">
                <a href="" class="block py-2 px-4 hover:bg-gray-700 rounded">Home</a>
                <a href="" class="block py-2 px-4 hover:bg-gray-700 rounded">Download YTMp3</a>
                <a href="" class="block py-2 px-4 hover:bg-gray-700 rounded">Contact</a>
            </nav>
        </div>
    </div>
    <!-- End of Navbar -->

    <!-- Header -->
    <div class="text-center text-white pt-10 pb-6">
